import React, { useEffect, useState } from 'react';
import api from '../services/api';

function Dashboard() {
    const [subscriptions, setSubscriptions] = useState([]);
    const [purchases, setPurchases] = useState([]);
    const [userData, setUserData] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [subscriptionsResponse, purchasesResponse, userResponse] = await Promise.all([
                    api.get('/subscriptions'),
                    api.get('/courses'),
                    api.get('/myid'),
                ]);
                setSubscriptions(subscriptionsResponse.data);
                setPurchases(purchasesResponse.data);
                setUserData(userResponse.data);
            } catch (err) {
                setError('Failed to load data.');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const handleCancelSubscription = async (id) => {
        try {
            await api.delete(`/subscriptions/${id}`);
            setSubscriptions(subscriptions.filter((sub) => sub.id !== id));
            alert('Subscription successfully canceled.');
        } catch (err) {
            alert('Failed to cancel subscription.');
        }
    };

    if (loading) {
        return <div>Loading your dashboard...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h1>Dashboard</h1>

            <h2>User Info</h2>
            <p><strong>Name:</strong> {userData.name}</p>
            <p><strong>Email:</strong> {userData.email}</p>

            <h2>Your Subscriptions</h2>
            {subscriptions.length > 0 ? (
                <ul>
                    {subscriptions.map((sub) => (
                        <li key={sub.id}>
                            <p><strong>Type:</strong> {sub.subscription_type_id}</p>
                            <p><strong>Status:</strong> {sub.status}</p>
                            <p><strong>Start Date:</strong> {sub.start_date}</p>
                            <p><strong>End Date:</strong> {sub.end_date}</p>
                            {sub.status === 'Active' && (
                                <button onClick={() => handleCancelSubscription(sub.id)}>
                                    Cancel Subscription
                                </button>
                            )}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You have no subscriptions in your dashboard.</p>
            )}

            <h2>Your Purchased Courses</h2>
            {purchases.length > 0 ? (
                <ul>
                    {purchases.map((purchase) => (
                        <li key={purchase.id}>
                            <p><strong>Course ID:</strong> {purchase.course_id}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You have no courses in your dashboard.</p>
            )}
        </div>
    );
}

export default Dashboard;
