import axios from 'axios';

const api = axios.create({
    baseURL: 'https://fucklaravel.ru/api',
    headers: {
        'Content-Type': 'application/json', // Говорит серверу, что мы отправляем JSON
    },
});


// Перехватчик для токена
api.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('authToken');
        if (token) {
            config.headers.Authorization = `Bearer ${token}`; // Добавляет токен в заголовки
        }
        return config;
    },
    (error) => Promise.reject(error)
);



// Auth
export const login = (email, password) => {
    return api.post('/access-token', { email, password });
};

export const register = (name, email, password) => {
    return api.post('/usercus', { name, email, password });
};

export const fetchCourses = () => api.get('/courses');

export const createCourse = (data) => {
    return api.post('/courses', {
        title: data.title,
        description: data.description,
        price: data.price,
        is_active: data.is_active,
        is_subscription_based: data.is_subscription_based,
    });
};

export const updateCourse = (id, data) => {
    return api.put(`/courses/${id}`, {
        title: data.title,
        description: data.description,
        price: data.price,
        is_active: data.is_active,
        is_subscription_based: data.is_subscription_based,
    });
};

export const deleteCourse = (id) => api.delete(`/courses/${id}`);

// Subscriptions
export const fetchSubscriptions = () => api.get('/subscriptions');

export const createSubscription = (data) => {
    return api.post('/subscriptions', {
        user_id: data.user_id,
        subscription_type_id: data.subscription_type_id,
        start_date: data.start_date,
        end_date: data.end_date,
        status: data.status,
    });
};

export const updateSubscription = (id, data) => {
    return api.put(`/subscriptions/${id}`, {
        user_id: data.user_id,
        subscription_type_id: data.subscription_type_id,
        start_date: data.start_date,
        end_date: data.end_date,
        status: data.status,
    });
};

export const deleteSubscription = (id) => api.delete(`/subscriptions/${id}`);

// Articles
export const fetchArticles = () => api.get('/articles');

export const createArticle = (data) => {
    return api.post('/articles', {
        title: data.title,
        content: data.content,
        course_id: data.course_id,
    });
};

export const updateArticle = (id, data) => {
    return api.put(`/articles/${id}`, {
        title: data.title,
        content: data.content,
        course_id: data.course_id,
    });
};

export const deleteArticle = (id) => api.delete(`/articles/${id}`);

// User
export const fetchUserData = () => api.get('/myid');

export const updateUserData = (data) => {
    return api.put('/myid', {
        name: data.name,
        email: data.email,
        is_active: data.is_active,
    });
};

export default api;
