import React, { useEffect, useState } from 'react';
import api from '../services/api';
import Modal from '../components/Modal';
import '../styles/AdminPanel.css';
import { useNavigate } from "react-router-dom";

function AdminPanel() {
    const [data, setData] = useState({
        users: [],
        courses: [],
        articles: [],
        subscriptions: [],
        subscriptionTypes: [],
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [isModalOpen, setModalOpen] = useState(false);
    const [currentEdit, setCurrentEdit] = useState(null);
    const [editData, setEditData] = useState({});
    const [isAdding, setIsAdding] = useState(false);
    const navigate = useNavigate();

    const templates = {
        users: { name: '', email: '', password: '', is_active: true },
        courses: { title: '', description: '', price: 0, is_active: true, is_subscription_based: true },
        articles: { course_id: '', title: '', content: '' },
        subscriptions: { user_id: '', subscription_type_id: '', start_date: '', end_date: '' },
        subscriptionTypes: { name: '', description: '', price: 0, is_active: false },
    };

    const fieldTranslations = {
        users: {
            name: 'Имя',
            email: 'Электронная почта',
            password: 'Пароль',
            is_active: 'Активен',
            created_at: 'Дата создания',
            updated_at: 'Дата обновления',
        },
        courses: {
            title: 'Название',
            description: 'Описание',
            price: 'Цена',
            is_active: 'Активен',
            is_subscription_based: 'На основе подписки',
            created_at: 'Дата создания',
            updated_at: 'Дата обновления',
        },
        articles: {
            course_id: 'ID курса',
            title: 'Название',
            content: 'Содержание',
            created_at: 'Дата создания',
            updated_at: 'Дата обновления',
        },
        subscriptions: {
            user_id: 'ID пользователя',
            subscription_type_id: 'ID типа подписки',
            start_date: 'Дата начала',
            end_date: 'Дата окончания',
            created_at: 'Дата создания',
            updated_at: 'Дата обновления',
        },
        subscriptionTypes: {
            name: 'Название',
            description: 'Описание',
            price: 'Цена',
            is_active: 'Активен',
            created_at: 'Дата создания',
            updated_at: 'Дата обновления',
        },
    };

    const endpoints = {
        users: '/usercus',
        courses: '/courses',
        articles: '/articles',
        subscriptions: '/subscriptions',
        subscriptionTypes: '/subscription-types',
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const responses = await Promise.all(
                    Object.entries(endpoints).map(([key, endpoint]) =>
                        api.get(endpoint)
                    )
                );
                const newData = responses.reduce((acc, res, idx) => {
                    const key = Object.keys(endpoints)[idx];
                    acc[key] = res.data;
                    return acc;
                }, {});
                setData(newData);
            } catch (err) {
                setError('Не удалось загрузить данные админ-панели');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const logout = () => {
        localStorage.removeItem('authToken');
        navigate('/login');
    };

    const handleAdd = (key) => {
        setIsAdding(true);
        setCurrentEdit(key);
        setEditData({ ...templates[key] });
        setModalOpen(true);
    };

    const handleEdit = (key, item) => {
        setIsAdding(false);
        setCurrentEdit(key);
        setEditData({ ...item });
        setModalOpen(true);
    };

    const handleDelete = async (key, id) => {
        try {
            await api.delete(`${endpoints[key]}/${id}`);
            setData((prev) => ({
                ...prev,
                [key]: prev[key].filter((item) => item.id !== id),
            }));
        } catch (err) {
            alert('Не удалось удалить элемент');
        }
    };

    const handleSave = async () => {
        try {
            const endpoint = endpoints[currentEdit];
            if (isAdding) {
                // Мгновенное обновление данных в UI
                const tempId = `temp-${Date.now()}`; // Временный ID для UI
                const newData = { ...editData, id: tempId };

                setData((prev) => ({
                    ...prev,
                    [currentEdit]: [...prev[currentEdit], newData],
                }));

                // Отправка данных на сервер
                const response = await api.post(endpoint, editData);

                // Синхронизация с реальным ID от сервера
                setData((prev) => ({
                    ...prev,
                    [currentEdit]: prev[currentEdit].map((item) =>
                        item.id === tempId ? { ...response.data } : item
                    ),
                }));
                alert('Элемент успешно добавлен');
            } else {
                // Обновление существующей записи
                await api.put(`${endpoint}/${editData.id}`, editData);
                setData((prev) => ({
                    ...prev,
                    [currentEdit]: prev[currentEdit].map((item) =>
                        item.id === editData.id ? editData : item
                    ),
                }));
                alert('Изменения успешно сохранены');
            }
        } catch (err) {
            alert('Не удалось сохранить изменения');
        } finally {
            setModalOpen(false);
        }
    };

    if (loading) return <div className="admin-panel-container">Загрузка...</div>;
    if (error) return <div className="admin-panel-container">Ошибка: {error}</div>;

    return (
        <div className="admin-panel-container">
            <h1>Админ-панель</h1>
            <button onClick={logout} className="logout-button">
                Выйти из аккаунта
            </button>
            {Object.entries(data).map(([key, items]) => (
                <section key={key} className="admin-section">
                    <h2>
                        {{
                            users: 'Пользователи',
                            courses: 'Курсы',
                            articles: 'Статьи',
                            subscriptions: 'Подписки',
                            subscriptionTypes: 'Типы подписок',
                        }[key]}
                    </h2>
                    <button
                        className="admin-add-button"
                        onClick={() => handleAdd(key)}
                    >
                        Добавить {{
                            users: 'пользователя',
                            courses: 'курс',
                            articles: 'статью',
                            subscriptions: 'подписку',
                            subscriptionTypes: 'тип подписки',
                        }[key]}
                    </button>
                    <ul className="admin-list">
                        {items.map((item) => (
                            <li key={item.id} className="admin-list-item">
                                <span>
                                    {Object.values(item).slice(0, 2).join(' - ')}
                                </span>
                                <div className="admin-buttons">
                                    <button
                                        className="admin-edit-button"
                                        onClick={() => handleEdit(key, item)}
                                    >
                                        Редактировать
                                    </button>
                                    <button
                                        className="admin-delete-button"
                                        onClick={() => handleDelete(key, item.id)}
                                    >
                                        Удалить
                                    </button>
                                </div>
                            </li>
                        ))}
                    </ul>
                </section>
            ))}

            <Modal isOpen={isModalOpen} onClose={() => setModalOpen(false)}>
                <h2>
                    {isAdding
                        ? `Добавить ${
                              {
                                  users: 'пользователя',
                                  courses: 'курс',
                                  articles: 'статью',
                                  subscriptions: 'подписку',
                                  subscriptionTypes: 'тип подписки',
                              }[currentEdit]
                          }`
                        : `Редактировать ${
                              {
                                  users: 'пользователя',
                                  courses: 'курс',
                                  articles: 'статью',
                                  subscriptions: 'подписку',
                                  subscriptionTypes: 'тип подписки',
                              }[currentEdit]
                          }`}
                </h2>
                {editData &&
                    Object.keys(editData).map((field) => (
                        <label key={field} className="modal-input-label">
                            {fieldTranslations[currentEdit]?.[field] || field}:
                            {field === 'content' ? (
                                <textarea
                                    name={field}
                                    value={editData[field]}
                                    onChange={(e) => {
                                        const { name, value } = e.target;
                                        setEditData((prev) => ({
                                            ...prev,
                                            [name]: value,
                                        }));
                                    }}
                                    className="modal-textarea"
                                    rows="5"
                                />
                            ) : (
                                <input
                                    type={
                                        typeof editData[field] === 'boolean'
                                            ? 'checkbox'
                                            : field.includes('date')
                                            ? 'date'
                                            : 'text'
                                    }
                                    name={field}
                                    value={
                                        typeof editData[field] === 'boolean' ? undefined : editData[field]
                                    }
                                    checked={
                                        typeof editData[field] === 'boolean' ? editData[field] : undefined
                                    }
                                    onChange={(e) => {
                                        const { name, value, type, checked } = e.target;
                                        setEditData((prev) => ({
                                            ...prev,
                                            [name]: type === 'checkbox' ? checked : value,
                                        }));
                                    }}
                                />
                            )}
                        </label>
                    ))}
                <button className="modal-save-button" onClick={handleSave}>
                    Сохранить
                </button>
            </Modal>
        </div>
    );
}

export default AdminPanel;
