import React, { useEffect, useState } from 'react';
import api from '../services/api';
import Modal from '../components/Modal';
import {
    Editor,
    EditorState,
    RichUtils,
    convertToRaw,
    convertFromRaw,
} from 'draft-js';
import 'draft-js/dist/Draft.css';
import '../styles/AdminPanel.css';

function AdminPanel() {
    const [users, setUsers] = useState([]);
    const [courses, setCourses] = useState([]);
    const [articles, setArticles] = useState([]);
    const [subscriptions, setSubscriptions] = useState([]);
    const [subscriptionTypes, setSubscriptionTypes] = useState([]);

    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [isModalOpen, setModalOpen] = useState(false);
    const [isEditingArticle, setIsEditingArticle] = useState(false);
    const [currentArticle, setCurrentArticle] = useState({
        title: '',
        content: EditorState.createEmpty(),
        course_id: '',
    });

    const [currentEdit, setCurrentEdit] = useState(null); // Текущий элемент редактирования
    const [editData, setEditData] = useState({}); // Данные для редактирования в модальном окне

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [
                    usersRes,
                    coursesRes,
                    articlesRes,
                    subscriptionsRes,
                    subscriptionTypesRes,
                ] = await Promise.all([
                    api.get('/usercus'),
                    api.get('/courses'),
                    api.get('/articles'),
                    api.get('/subscriptions'),
                    api.get('/subscription-types'),
                ]);
                setUsers(usersRes.data);
                setCourses(coursesRes.data);
                setArticles(
                    articlesRes.data.map((article) => ({
                        ...article,
                        content: EditorState.createWithContent(
                            convertFromRaw(JSON.parse(article.content))
                        ),
                    }))
                );
                setSubscriptions(subscriptionsRes.data);
                setSubscriptionTypes(subscriptionTypesRes.data);
            } catch (err) {
                setError('Failed to load admin data');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const createHandler = async (endpoint, data, setState, resetState) => {
        try {
            const response = await api.post(endpoint, data);
            setState((prev) => [...prev, response.data]);
            resetState();
        } catch (err) {
            alert(`Failed to create ${endpoint}`);
        }
    };

    const deleteHandler = async (endpoint, id, setState) => {
        try {
            await api.delete(`${endpoint}/${id}`);
            setState((prev) => prev.filter((item) => item.id !== id));
        } catch (err) {
            alert(`Failed to delete ${endpoint}`);
        }
    };

    const handleEdit = (item, endpoint) => {
        setCurrentEdit(endpoint);
        setEditData(item);
        setModalOpen(true);
    };

    const handleSave = async () => {
        try {
            await api.put(`/${currentEdit}/${editData.id}`, editData);
            alert('Изменения успешно сохранены.');

            switch (currentEdit) {
                case 'usercus':
                    setUsers((prev) =>
                        prev.map((user) => (user.id === editData.id ? editData : user))
                    );
                    break;
                case 'courses':
                    setCourses((prev) =>
                        prev.map((course) => (course.id === editData.id ? editData : course))
                    );
                    break;
                case 'subscriptions':
                    setSubscriptions((prev) =>
                        prev.map((sub) => (sub.id === editData.id ? editData : sub))
                    );
                    break;
                case 'subscription-types':
                    setSubscriptionTypes((prev) =>
                        prev.map((type) => (type.id === editData.id ? editData : type))
                    );
                    break;
                default:
                    break;
            }

            setModalOpen(false);
        } catch (err) {
            alert('Не удалось сохранить изменения.');
        }
    };

    const openArticleModal = (article = null) => {
        if (article) {
            setIsEditingArticle(true);
            setCurrentArticle(article);
        } else {
            setIsEditingArticle(false);
            setCurrentArticle({
                title: '',
                content: EditorState.createEmpty(),
                course_id: '',
            });
        }
        setModalOpen(true);
    };

    const handleSaveArticle = async () => {
        try {
            const contentState = currentArticle.content.getCurrentContent();
            const rawContent = JSON.stringify(convertToRaw(contentState));

            const articleData = {
                title: currentArticle.title,
                content: rawContent,
                course_id: currentArticle.course_id,
            };

            if (isEditingArticle) {
                await api.put(`/articles/${currentArticle.id}`, articleData);
                setArticles((prev) =>
                    prev.map((article) =>
                        article.id === currentArticle.id
                            ? { ...currentArticle, content: articleData.content }
                            : article
                    )
                );
                alert('Статья успешно обновлена.');
            } else {
                const response = await api.post('/articles', articleData);
                setArticles((prev) => [
                    ...prev,
                    { ...response.data, content: articleData.content },
                ]);
                alert('Статья успешно добавлена.');
            }
            setModalOpen(false);
        } catch (err) {
            alert('Не удалось сохранить статью.');
        }
    };

    const handleEditorChange = (editorState) => {
        setCurrentArticle((prev) => ({
            ...prev,
            content: editorState,
        }));
    };

    const toggleInlineStyle = (style) => {
        setCurrentArticle((prev) => ({
            ...prev,
            content: RichUtils.toggleInlineStyle(prev.content, style),
        }));
    };

    if (loading) return <div className="admin-panel-container">Загрузка...</div>;
    if (error) return <div className="admin-panel-container">Ошибка: {error}</div>;

    return (
        <div className="admin-panel-container">
            <h1>Admin Panel</h1>

            {/* Articles Section */}
            <section className="admin-section">
                <h2>Articles</h2>
                <button
                    className="admin-add-button"
                    onClick={() => openArticleModal()}
                >
                    Add Article
                </button>
                <ul className="admin-list">
                    {articles.map((article) => (
                        <li key={article.id} className="admin-list-item">
                            <span>{article.title}</span>
                            <div className="admin-buttons">
                                <button
                                    className="admin-edit-button"
                                    onClick={() => openArticleModal(article)}
                                >
                                    Edit
                                </button>
                                <button
                                    className="admin-delete-button"
                                    onClick={() =>
                                        deleteHandler('/articles', article.id, setArticles)
                                    }
                                >
                                    Delete
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            </section>

            {/* Modal for Article */}
            <Modal isOpen={isModalOpen} onClose={() => setModalOpen(false)}>
                <h2>{isEditingArticle ? 'Edit Article' : 'Add Article'}</h2>
                <label>
                    Title:
                    <input
                        type="text"
                        value={currentArticle.title}
                        onChange={(e) =>
                            setCurrentArticle((prev) => ({
                                ...prev,
                                title: e.target.value,
                            }))
                        }
                    />
                </label>
                <label>
                    Content:
                    <div className="editor-toolbar">
                        <button
                            onMouseDown={(e) => {
                                e.preventDefault();
                                toggleInlineStyle('BOLD');
                            }}
                        >
                            Bold
                        </button>
                        <button
                            onMouseDown={(e) => {
                                e.preventDefault();
                                toggleInlineStyle('ITALIC');
                            }}
                        >
                            Italic
                        </button>
                        <button
                            onMouseDown={(e) => {
                                e.preventDefault();
                                toggleInlineStyle('UNDERLINE');
                            }}
                        >
                            Underline
                        </button>
                    </div>
                    <Editor
                        editorState={currentArticle.content}
                        onChange={handleEditorChange}
                    />
                </label>
                <label>
                    Course:
                    <select
                        value={currentArticle.course_id}
                        onChange={(e) =>
                            setCurrentArticle((prev) => ({
                                ...prev,
                                course_id: e.target.value,
                            }))
                        }
                    >
                        <option value="">Select Course</option>
                        {courses.map((course) => (
                            <option key={course.id} value={course.id}>
                                {course.title}
                            </option>
                        ))}
                    </select>
                </label>
                <button className="modal-save-button" onClick={handleSaveArticle}>
                    Save
                </button>
            </Modal>
        </div>
    );
}

export default AdminPanel;
