import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/SubscriptionForm.css';
import { createSubscription } from '../services/api'; // Импорт функции API для создания подписки

function SubscriptionForm() {
    const location = useLocation();
    const navigate = useNavigate();
    const subscription = location.state?.subscription; // Данные выбранной подписки
    const userId = localStorage.getItem('userId'); // Получаем ID текущего пользователя из localStorage

    const [cardNumber, setCardNumber] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [cvc, setCvc] = useState('');
    const [errors, setErrors] = useState({});
    const [paymentSuccess, setPaymentSuccess] = useState(false);
    const [loading, setLoading] = useState(false);

    // Валидация номера карты
    const validateCardNumber = (number) => /^\d{16}$/.test(number);

    // Валидация срока действия карты
    const validateExpiryDate = (date) => {
        const [month, year] = date.split('/');
        const isValidFormat = /^\d{2}\/\d{2}$/.test(date); // Проверяем формат MM/YY
        const isValidMonth = month >= 1 && month <= 12; // Проверяем, что месяц от 1 до 12
        const currentYear = new Date().getFullYear() % 100; // Берем последние две цифры текущего года
        const isValidYear = year >= currentYear; // Проверяем, что год >= текущего
        return isValidFormat && isValidMonth && isValidYear;
    };

    // Валидация CVC
    const validateCvc = (code) => /^\d{3}$/.test(code);

    // Обработка изменения срока действия карты
    const handleExpiryDateChange = (e) => {
        let value = e.target.value.replace(/[^0-9]/g, ''); // Убираем все символы кроме цифр
        if (value.length > 2) {
            value = value.slice(0, 2) + '/' + value.slice(2); // Автоматически добавляем слэш
        }
        setExpiryDate(value.slice(0, 5)); // Ограничиваем длину до 5 символов
    };

    // Обработка отправки формы
    const handleSubmit = async (e) => {
        e.preventDefault();
        const newErrors = {};

        // Проверка валидности полей
        if (!validateCardNumber(cardNumber)) newErrors.cardNumber = 'Номер карты должен содержать 16 цифр.';
        if (!validateExpiryDate(expiryDate)) newErrors.expiryDate = 'Срок действия карты должен быть в формате MM/YY.';
        if (!validateCvc(cvc)) newErrors.cvc = 'CVC должен содержать 3 цифры.';

        setErrors(newErrors);

        if (Object.keys(newErrors).length === 0) {
            try {
                setLoading(true);

                // Проверка наличия userId
                if (!userId) {
                    alert('Ошибка: Не удалось определить пользователя.');
                    return;
                }

                // Подготовка данных для создания подписки
                const currentDate = new Date();
                const startDate = currentDate.toISOString().split('T')[0]; // Сегодняшняя дата
                const endDate = new Date(currentDate.setMonth(currentDate.getMonth() + 1))
                    .toISOString()
                    .split('T')[0]; // Дата через месяц

                // Отправка запроса на создание подписки
                await createSubscription({
                    user_id: parseInt(userId, 10), // Передаем user_id как число
                    subscription_type_id: subscription.id, // ID выбранной подписки
                    start_date: startDate, // Начало подписки
                    end_date: endDate, // Конец подписки
                    status: 'active', // Статус подписки
                });

                // Успешная оплата
                setPaymentSuccess(true);

                // Перенаправление на страницу подписок через 3 секунды
                setTimeout(() => {
                    navigate('/subscriptions');
                }, 3000);
            } catch (error) {
                console.error('Ошибка при создании подписки:', error.response?.data || error);
                const apiErrors = error.response?.data?.errors;

                // Отображение ошибок от API
                if (apiErrors) {
                    setErrors({
                        user_id: apiErrors.user_id?.[0],
                        status: apiErrors.status?.[0],
                    });
                } else {
                    alert('Произошла ошибка при создании подписки.');
                }
            } finally {
                setLoading(false);
            }
        }
    };

    return (
        <div className="subscription-form-container">
            {paymentSuccess ? (
                <div className="payment-success">
                    <h1>Оплата прошла успешно!</h1>
                    <p>Подписка активирована. Вы будете перенаправлены на страницу подписок через несколько секунд...</p>
                </div>
            ) : (
                <>
                    <h1>Оформление подписки</h1>
                    <p>Вы выбрали подписку: {subscription.name}</p>
                    <form onSubmit={handleSubmit}>
                        <label>
                            Номер карты:
                            <input
                                type="text"
                                value={cardNumber}
                                onChange={(e) => setCardNumber(e.target.value.replace(/\D/g, ''))} // Только цифры
                                placeholder="1234 5678 9012 3456"
                                maxLength="16"
                                required
                            />
                            {errors.cardNumber && <span className="error">{errors.cardNumber}</span>}
                        </label>
                        <label>
                            Срок действия (MM/YY):
                            <input
                                type="text"
                                value={expiryDate}
                                onChange={handleExpiryDateChange}
                                placeholder="MM/YY"
                                required
                            />
                            {errors.expiryDate && <span className="error">{errors.expiryDate}</span>}
                        </label>
                        <label>
                            CVC:
                            <input
                                type="text"
                                value={cvc}
                                onChange={(e) => setCvc(e.target.value.replace(/\D/g, ''))} // Только цифры
                                placeholder="123"
                                maxLength="3"
                                required
                            />
                            {errors.cvc && <span className="error">{errors.cvc}</span>}
                        </label>
                        <button type="submit" disabled={loading}>
                            {loading ? 'Обработка...' : 'Оплатить'}
                        </button>
                    </form>
                </>
            )}
        </div>
    );
}

export default SubscriptionForm;
