import React from 'react';
import { useLocation } from 'react-router-dom';

function PurchaseForm() {
    const location = useLocation();
    const { courseId } = location.state || {};

    const handleSubmit = (e) => {
        e.preventDefault();
        // Здесь логика для покупки курса
        alert(`Курс с ID: ${courseId} успешно приобретён.`);
    };

    return (
        <div className="form-container">
            <h1>Приобрести курс</h1>
            <form onSubmit={handleSubmit}>
                <p>Вы собираетесь приобрести курс с ID: {courseId}</p>
                <button type="submit" className="form-submit-button">Купить курс</button>
            </form>
        </div>
    );
}

export default PurchaseForm;
