import React, { useEffect, useState } from 'react';
import api from '../services/api';

function AdminPanel() {
    const [users, setUsers] = useState([]);
    const [courses, setCourses] = useState([]);
    const [articles, setArticles] = useState([]);
    const [subscriptions, setSubscriptions] = useState([]);
    const [subscriptionTypes, setSubscriptionTypes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [newUser, setNewUser] = useState({ name: '', email: '', is_active: true });
    const [newCourse, setNewCourse] = useState({
        title: '',
        description: '',
        price: '',
        is_active: false,
        is_subscription_based: false,
    });
    const [newArticle, setNewArticle] = useState({ title: '', content: '', course_id: '' });
    const [newSubscription, setNewSubscription] = useState({
        user_id: '',
        subscription_type_id: '',
        start_date: '',
        end_date: '',
        status: 'Active',
    });
    const [newSubscriptionType, setNewSubscriptionType] = useState({
        name: '',
        description: '',
        price: '',
        is_active: true,
    });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [
                    usersRes,
                    coursesRes,
                    articlesRes,
                    subscriptionsRes,
                    subscriptionTypesRes,
                ] = await Promise.all([
                    api.get('/usercus'),
                    api.get('/courses'),
                    api.get('/articles'),
                    api.get('/subscriptions'),
                    api.get('/subscription-types'),
                ]);
                setUsers(usersRes.data);
                setCourses(coursesRes.data);
                setArticles(articlesRes.data);
                setSubscriptions(subscriptionsRes.data);
                setSubscriptionTypes(subscriptionTypesRes.data);
            } catch (err) {
                setError('Failed to load admin data');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const handleCreateEntity = async (apiMethod, data, setState, resetState) => {
        try {
            const response = await apiMethod(data);
            setState((prev) => [...prev, response.data]);
            if (resetState) resetState();
        } catch (err) {
            alert('Failed to create entity');
        }
    };

    const handleDeleteEntity = async (apiMethod, id, setState) => {
        try {
            await apiMethod(id);
            setState((prev) => prev.filter((item) => item.id !== id));
        } catch (err) {
            alert('Failed to delete entity');
        }
    };

    if (loading) {
        return <div>Loading admin panel...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h1>Admin Panel</h1>

            {/* Users Section */}
            <section>
                <h2>Users</h2>
                <form
                    onSubmit={(e) => {
                        e.preventDefault();
                        handleCreateEntity(api.createUser, newUser, setUsers, () =>
                            setNewUser({ name: '', email: '', is_active: true })
                        );
                    }}
                >
                    <label>
                        Name:
                        <input
                            type="text"
                            value={newUser.name}
                            onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
                            required
                        />
                    </label>
                    <br />
                    <label>
                        Email:
                        <input
                            type="email"
                            value={newUser.email}
                            onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                            required
                        />
                    </label>
                    <br />
                    <label>
                        Active:
                        <input
                            type="checkbox"
                            checked={newUser.is_active}
                            onChange={(e) => setNewUser({ ...newUser, is_active: e.target.checked })}
                        />
                    </label>
                    <br />
                    <button type="submit">Add User</button>
                </form>
                <ul>
                    {users.map((user) => (
                        <li key={user.id}>
                            {user.name} - {user.email}
                            <button
                                onClick={() =>
                                    handleDeleteEntity(api.deleteUser, user.id, setUsers)
                                }
                            >
                                Delete
                            </button>
                        </li>
                    ))}
                </ul>
            </section>

            {/* Courses Section */}
            <section>
                <h2>Courses</h2>
                <form
                    onSubmit={(e) => {
                        e.preventDefault();
                        handleCreateEntity(api.createCourse, newCourse, setCourses, () =>
                            setNewCourse({
                                title: '',
                                description: '',
                                price: '',
                                is_active: false,
                                is_subscription_based: false,
                            })
                        );
                    }}
                >
                    <label>
                        Title:
                        <input
                            type="text"
                            value={newCourse.title}
                            onChange={(e) => setNewCourse({ ...newCourse, title: e.target.value })}
                            required
                        />
                    </label>
                    <br />
                    <label>
                        Description:
                        <textarea
                            value={newCourse.description}
                            onChange={(e) =>
                                setNewCourse({ ...newCourse, description: e.target.value })
                            }
                            required
                        />
                    </label>
                    <br />
                    <label>
                        Price:
                        <input
                            type="number"
                            value={newCourse.price}
                            onChange={(e) =>
                                setNewCourse({ ...newCourse, price: parseFloat(e.target.value) })
                            }
                            required
                            min="0"
                        />
                    </label>
                    <br />
                    <label>
                        Active:
                        <input
                            type="checkbox"
                            checked={newCourse.is_active}
                            onChange={(e) =>
                                setNewCourse({ ...newCourse, is_active: e.target.checked })
                            }
                        />
                    </label>
                    <br />
                    <label>
                        Subscription Based:
                        <input
                            type="checkbox"
                            checked={newCourse.is_subscription_based}
                            onChange={(e) =>
                                setNewCourse({
                                    ...newCourse,
                                    is_subscription_based: e.target.checked,
                                })
                            }
                        />
                    </label>
                    <br />
                    <button type="submit">Add Course</button>
                </form>
                <ul>
                    {courses.map((course) => (
                        <li key={course.id}>
                            {course.title} - ${course.price}
                            <button
                                onClick={() =>
                                    handleDeleteEntity(api.deleteCourse, course.id, setCourses)
                                }
                            >
                                Delete
                            </button>
                        </li>
                    ))}
                </ul>
            </section>

            {/* Articles Section */}
            <section>
                <h2>Articles</h2>
                <form
                    onSubmit={(e) => {
                        e.preventDefault();
                        handleCreateEntity(api.createArticle, newArticle, setArticles, () =>
                            setNewArticle({ title: '', content: '', course_id: '' })
                        );
                    }}
                >
                    <label>
                        Title:
                        <input
                            type="text"
                            value={newArticle.title}
                            onChange={(e) =>
                                setNewArticle({ ...newArticle, title: e.target.value })
                            }
                            required
                        />
                    </label>
                    <br />
                    <label>
                        Content:
                        <textarea
                            value={newArticle.content}
                            onChange={(e) =>
                                setNewArticle({ ...newArticle, content: e.target.value })
                            }
                            required
                        />
                    </label>
                    <br />
                    <label>
                        Course:
                        <select
                            value={newArticle.course_id}
                            onChange={(e) =>
                                setNewArticle({ ...newArticle, course_id: e.target.value })
                            }
                            required
                        >
                            <option value="">Select a course</option>
                            {courses.map((course) => (
                                <option key={course.id} value={course.id}>
                                    {course.title}
                                </option>
                            ))}
                        </select>
                    </label>
                    <br />
                    <button type="submit">Add Article</button>
                </form>
                <ul>
                    {articles.map((article) => (
                        <li key={article.id}>
                            {article.title}
                            <button
                                onClick={() =>
                                    handleDeleteEntity(api.deleteArticle, article.id, setArticles)
                                }
                            >
                                Delete
                            </button>
                        </li>
                    ))}
                </ul>
            </section>

            {/* Subscription Types Section */}
            <section>
                <h2>Subscription Types</h2>
                <form
                    onSubmit={(e) => {
                        e.preventDefault();
                        handleCreateEntity(
                            api.createSubscriptionType,
                            newSubscriptionType,
                            setSubscriptionTypes,
                            () =>
                                setNewSubscriptionType({
                                    name: '',
                                    description: '',
                                    price: '',
                                    is_active: true,
                                })
                        );
                    }}
                >
                    <label>
                        Name:
                        <input
                            type="text"
                            value={newSubscriptionType.name}
                            onChange={(e) =>
                                setNewSubscriptionType({
                                    ...newSubscriptionType,
                                    name: e.target.value,
                                })
                            }
                            required
                        />
                    </label>
                    <br />
                    <label>
                        Description:
                        <textarea
                            value={newSubscriptionType.description}
                            onChange={(e) =>
                                setNewSubscriptionType({
                                    ...newSubscriptionType,
                                    description: e.target.value,
                                })
                            }
                        />
                    </label>
                    <br />
                    <label>
                        Price:
                        <input
                            type="number"
                            value={newSubscriptionType.price}
                            onChange={(e) =>
                                setNewSubscriptionType({
                                    ...newSubscriptionType,
                                    price: parseFloat(e.target.value),
                                })
                            }
                            required
                            min="0"
                        />
                    </label>
                    <br />
                    <label>
                        Active:
                        <input
                            type="checkbox"
                            checked={newSubscriptionType.is_active}
                            onChange={(e) =>
                                setNewSubscriptionType({
                                    ...newSubscriptionType,
                                    is_active: e.target.checked,
                                })
                            }
                        />
                    </label>
                    <br />
                    <button type="submit">Add Subscription Type</button>
                </form>
                <ul>
                    {subscriptionTypes.map((type) => (
                        <li key={type.id}>
                            {type.name} - ${type.price} - {type.is_active ? 'Active' : 'Inactive'}
                            <button
                                onClick={() =>
                                    handleDeleteEntity(
                                        api.deleteSubscriptionType,
                                        type.id,
                                        setSubscriptionTypes
                                    )
                                }
                            >
                                Delete
                            </button>
                        </li>
                    ))}
                </ul>
            </section>

            {/* Subscriptions Section */}
            <section>
                <h2>Subscriptions</h2>
                <form
                    onSubmit={(e) => {
                        e.preventDefault();
                        handleCreateEntity(
                            api.createSubscription,
                            newSubscription,
                            setSubscriptions,
                            () =>
                                setNewSubscription({
                                    user_id: '',
                                    subscription_type_id: '',
                                    start_date: '',
                                    end_date: '',
                                    status: 'Active',
                                })
                        );
                    }}
                >
                    <label>
                        User:
                        <select
                            value={newSubscription.user_id}
                            onChange={(e) =>
                                setNewSubscription({ ...newSubscription, user_id: e.target.value })
                            }
                            required
                        >
                            <option value="">Select a user</option>
                            {users.map((user) => (
                                <option key={user.id} value={user.id}>
                                    {user.name}
                                </option>
                            ))}
                        </select>
                    </label>
                    <br />
                    <label>
                        Subscription Type:
                        <select
                            value={newSubscription.subscription_type_id}
                            onChange={(e) =>
                                setNewSubscription({
                                    ...newSubscription,
                                    subscription_type_id: e.target.value,
                                })
                            }
                            required
                        >
                            <option value="">Select a subscription type</option>
                            {subscriptionTypes.map((type) => (
                                <option key={type.id} value={type.id}>
                                    {type.name}
                                </option>
                            ))}
                        </select>
                    </label>
                    <br />
                    <label>
                        Start Date:
                        <input
                            type="date"
                            value={newSubscription.start_date}
                            onChange={(e) =>
                                setNewSubscription({
                                    ...newSubscription,
                                    start_date: e.target.value,
                                })
                            }
                            required
                        />
                    </label>
                    <br />
                    <label>
                        End Date:
                        <input
                            type="date"
                            value={newSubscription.end_date}
                            onChange={(e) =>
                                setNewSubscription({
                                    ...newSubscription,
                                    end_date: e.target.value,
                                })
                            }
                            required
                        />
                    </label>
                    <br />
                    <label>
                        Status:
                        <select
                            value={newSubscription.status}
                            onChange={(e) =>
                                setNewSubscription({ ...newSubscription, status: e.target.value })
                            }
                            required
                        >
                            <option value="Active">Active</option>
                            <option value="Paused">Paused</option>
                            <option value="Expired">Expired</option>
                        </select>
                    </label>
                    <br />
                    <button type="submit">Add Subscription</button>
                </form>
                <ul>
                    {subscriptions.map((sub) => (
                        <li key={sub.id}>
                            User: {users.find((u) => u.id === sub.user_id)?.name}, Type:{' '}
                            {subscriptionTypes.find((type) => type.id === sub.subscription_type_id)
                                ?.name || 'Unknown'}, Status: {sub.status}
                            <button
                                onClick={() =>
                                    handleDeleteEntity(api.deleteSubscription, sub.id, setSubscriptions)
                                }
                            >
                                Delete
                            </button>
                        </li>
                    ))}
                </ul>
            </section>
        </div>
    );
}

export default AdminPanel;
