import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Отправляем данные для получения токена
            const response = await api.post('/access-token', { email, password });

            // Сохраняем токен в localStorage
            localStorage.setItem('authToken', response.data.token);

            // Получаем данные пользователя после входа
            const userResponse = await api.get('/myid', {
                headers: { Authorization: `Bearer ${response.data.token}` },
            });

            // Проверяем роль пользователя
            if (userResponse.data.role === 'admin') {
                navigate('/admin-panel'); // Перенаправляем в админ-панель
            } else {
                navigate('/dashboard'); // Перенаправляем в личный кабинет
            }
        } catch (err) {
            setError('Invalid credentials. Please try again.');
        }
    };

    return (
        <div>
            <h1>Login</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <label>
                    Email:
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </label>
                <br />
                <label>
                    Password:
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </label>
                <br />
                <button type="submit">Login</button>
            </form>
        </div>
    );
}

export default Login;
