import React, { useEffect, useState } from 'react';
import api from '../services/api';

function AdminPanel() {
    const [users, setUsers] = useState([]);
    const [courses, setCourses] = useState([]);
    const [articles, setArticles] = useState([]);
    const [subscriptions, setSubscriptions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [newUser, setNewUser] = useState({ name: '', email: '',password: '',  is_active: true });
    const [newCourse, setNewCourse] = useState({
        title: '',
        description: '',
        price: '',
        is_active: false,
        is_subscription_based: false,
    });
    const [newArticle, setNewArticle] = useState({ title: '', content: '', course_id: '' });
    const [newSubscription, setNewSubscription] = useState({
        user_id: '',
        subscription_type_id: '',
        start_date: '',
        end_date: '',
        status: 'Active',
    });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [usersRes, coursesRes, articlesRes, subscriptionsRes] = await Promise.all([
                    api.get('/usercus'),
                    api.get('/courses'),
                    api.get('/articles'),
                    api.get('/subscriptions'),
                ]);
                setUsers(usersRes.data);
                setCourses(coursesRes.data);
                setArticles(articlesRes.data);
                setSubscriptions(subscriptionsRes.data);
            } catch (err) {
                setError('Failed to load admin data');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    // CRUD Handlers
    const handleCreateUser = async () => {
        try {
            const response = await api.post('/usercus', newUser);
            setUsers([...users, response.data]);
            setNewUser({ name: '', email: '',password: '', is_active: true });
        } catch (err) {
            alert('Failed to create user');
        }
    };

    const handleDeleteUser = async (id) => {
        try {
            await api.delete(`/usercus/${id}`);
            setUsers(users.filter((user) => user.id !== id));
        } catch (err) {
            alert('Failed to delete user');
        }
    };

    const handleCreateCourse = async () => {
        try {
            const response = await api.post('/courses', newCourse);
            setCourses([...courses, response.data]);
            setNewCourse({
                title: '',
                description: '',
                price: '',
                is_active: false,
                is_subscription_based: false,
            });
        } catch (err) {
            alert('Failed to create course');
        }
    };

    const handleDeleteCourse = async (id) => {
        try {
            await api.delete(`/courses/${id}`);
            setCourses(courses.filter((course) => course.id !== id));
        } catch (err) {
            alert('Failed to delete course');
        }
    };

    const handleCreateArticle = async () => {
        try {
            const response = await api.post('/articles', newArticle);
            setArticles([...articles, response.data]);
            setNewArticle({ title: '', content: '', course_id: '' });
        } catch (err) {
            alert('Failed to create article');
        }
    };

    const handleDeleteArticle = async (id) => {
        try {
            await api.delete(`/articles/${id}`);
            setArticles(articles.filter((article) => article.id !== id));
        } catch (err) {
            alert('Failed to delete article');
        }
    };

    const handleCreateSubscription = async () => {
        try {
            const response = await api.post('/subscriptions', newSubscription);
            setSubscriptions([...subscriptions, response.data]);
            setNewSubscription({
                user_id: '',
                subscription_type_id: '',
                start_date: '',
                end_date: '',
                status: 'Active',
            });
        } catch (err) {
            alert('Failed to create subscription');
        }
    };

    const handleDeleteSubscription = async (id) => {
        try {
            await api.delete(`/subscriptions/${id}`);
            setSubscriptions(subscriptions.filter((sub) => sub.id !== id));
        } catch (err) {
            alert('Failed to delete subscription');
        }
    };

    if (loading) {
        return <div>Loading admin panel...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h1>Admin Panel</h1>

            {/* Users Section */}
            <h2>Users</h2>
            <form
                onSubmit={(e) => {
                    e.preventDefault();
                    handleCreateUser();
                }}
            >
                <label>
                    Name:
                    <input
                        type="text"
                        value={newUser.name}
                        onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
                        required
                    />
                </label>
                <br />
                <label>
                    Email:
                    <input
                        type="email"
                        value={newUser.email}
                        onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                        required
                    />
                </label>
                <br />
                <label>
                    Password:
                    <input
                        type="password"
                        value={newUser.password}
                        onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
                        required
                    />
                </label>
                <br />
                <label>
                    Active:
                    <input
                        type="checkbox"
                        checked={newUser.is_active}
                        onChange={(e) => setNewUser({ ...newUser, is_active: e.target.checked })}
                    />
                </label>
                <br />
                <button type="submit">Add User</button>
            </form>
            <ul>
                {users.map((user) => (
                    <li key={user.id}>
                        {user.name} - {user.email}
                        <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
                    </li>
                ))}
            </ul>

            {/* Courses Section */}
            <h2>Courses</h2>
            <form
                onSubmit={(e) => {
                    e.preventDefault();
                    handleCreateCourse();
                }}
            >
                <label>
                    Title:
                    <input
                        type="text"
                        value={newCourse.title}
                        onChange={(e) => setNewCourse({ ...newCourse, title: e.target.value })}
                        required
                    />
                </label>
                <br />
                <label>
                    Description:
                    <textarea
                        value={newCourse.description}
                        onChange={(e) =>
                            setNewCourse({ ...newCourse, description: e.target.value })
                        }
                        required
                    />
                </label>
                <br />
                <label>
                    Price:
                    <input
                        type="number"
                        value={newCourse.price}
                        onChange={(e) =>
                            setNewCourse({ ...newCourse, price: parseFloat(e.target.value) })
                        }
                        required
                        min="0"
                    />
                </label>
                <br />
                <label>
                    Is Active:
                    <input
                        type="checkbox"
                        checked={newCourse.is_active}
                        onChange={(e) =>
                            setNewCourse({ ...newCourse, is_active: e.target.checked })
                        }
                    />
                </label>
                <br />
                <label>
                    Is Subscription Based:
                    <input
                        type="checkbox"
                        checked={newCourse.is_subscription_based}
                        onChange={(e) =>
                            setNewCourse({
                                ...newCourse,
                                is_subscription_based: e.target.checked,
                            })
                        }
                    />
                </label>
                <br />
                <button type="submit">Add Course</button>
            </form>
            <ul>
                {courses.map((course) => (
                    <li key={course.id}>
                        {course.title}
                        <button onClick={() => handleDeleteCourse(course.id)}>Delete</button>
                    </li>
                ))}
            </ul>

            {/* Articles Section */}
            <h2>Articles</h2>
            <form
                onSubmit={(e) => {
                    e.preventDefault();
                    handleCreateArticle();
                }}
            >
                <label>
                    Title:
                    <input
                        type="text"
                        value={newArticle.title}
                        onChange={(e) => setNewArticle({ ...newArticle, title: e.target.value })}
                        required
                    />
                </label>
                <br />
                <label>
                    Content:
                    <textarea
                        value={newArticle.content}
                        onChange={(e) =>
                            setNewArticle({ ...newArticle, content: e.target.value })
                        }
                        required
                    />
                </label>
                <br />
                <label>
                    Course:
                    <select
                        value={newArticle.course_id}
                        onChange={(e) =>
                            setNewArticle({ ...newArticle, course_id: e.target.value })
                        }
                        required
                    >
                        <option value="">Select a course</option>
                        {courses.map((course) => (
                            <option key={course.id} value={course.id}>
                                {course.title}
                            </option>
                        ))}
                    </select>
                </label>
                <br />
                <button type="submit">Add Article</button>
            </form>
            <ul>
                {articles.map((article) => (
                    <li key={article.id}>
                        {article.title}
                        <button onClick={() => handleDeleteArticle(article.id)}>Delete</button>
                    </li>
                ))}
            </ul>

            {/* Subscriptions Section */}


            <h2>Subscriptions</h2>
            <form
                onSubmit={(e) => {
                    e.preventDefault();
                    handleCreateSubscription();
                }}
            >
                <label>
                    User:
                    <select
                        value={newSubscription.user_id}
                        onChange={(e) =>
                            setNewSubscription({ ...newSubscription, user_id: e.target.value })
                        }
                        required
                    >
                        <option value="">Select a user</option>
                        {users.map((user) => (
                            <option key={user.id} value={user.id}>
                                {user.name}
                            </option>
                        ))}
                    </select>
                </label>
                <br />
                <label>
                    Subscription Type:
                    <input
                        type="text"
                        value={newSubscription.subscription_type_id}
                        onChange={(e) =>
                            setNewSubscription({
                                ...newSubscription,
                                subscription_type_id: e.target.value,
                            })
                        }
                        required
                    />
                </label>
                <br />
                <label>
                    Start Date:
                    <input
                        type="date"
                        value={newSubscription.start_date}
                        onChange={(e) =>
                            setNewSubscription({ ...newSubscription, start_date: e.target.value })
                        }
                        required
                    />
                </label>
                <br />
                <label>
                    End Date:
                    <input
                        type="date"
                        value={newSubscription.end_date}
                        onChange={(e) =>
                            setNewSubscription({ ...newSubscription, end_date: e.target.value })
                        }
                        required
                    />
                </label>
                <br />
                <button type="submit">Add Subscription</button>
            </form>
            <ul>
                {subscriptions.map((sub) => (
                    <li key={sub.id}>
                        User: {sub.user_id}, Type: {sub.subscription_type_id}
                        <button onClick={() => handleDeleteSubscription(sub.id)}>Delete</button>
                    </li>
                ))}
            </ul>
            <h2>Subscription Types</h2>
            <form
                onSubmit={(e) => {
                    e.preventDefault();
                    handleCreateEntity(
                        api.createSubscriptionType,
                        newSubscriptionType,
                        setSubscriptionTypes,
                        () =>
                            setNewSubscriptionType({
                                name: '',
                                description: '',
                                price: '',
                                is_active: true,
                            })
                    );
                }}
            >
                <label>
                    Name:
                    <input
                        type="text"
                        value={newSubscriptionType.name}
                        onChange={(e) =>
                            setNewSubscriptionType({
                                ...newSubscriptionType,
                                name: e.target.value,
                            })
                        }
                        required
                    />
                </label>
                <br />
                <label>
                    Description:
                    <textarea
                        value={newSubscriptionType.description}
                        onChange={(e) =>
                            setNewSubscriptionType({
                                ...newSubscriptionType,
                                description: e.target.value,
                            })
                        }
                    />
                </label>
                <br />
                <label>
                    Price:
                    <input
                        type="number"
                        value={newSubscriptionType.price}
                        onChange={(e) =>
                            setNewSubscriptionType({
                                ...newSubscriptionType,
                                price: parseFloat(e.target.value),
                            })
                        }
                        required
                        min="0"
                    />
                </label>
                <br />
                <label>
                    Active:
                    <input
                        type="checkbox"
                        checked={newSubscriptionType.is_active}
                        onChange={(e) =>
                            setNewSubscriptionType({
                                ...newSubscriptionType,
                                is_active: e.target.checked,
                            })
                        }
                    />
                </label>
                <br />
                <button type="submit">Add Subscription Type</button>
            </form>
            <ul>
                {subscriptionTypes.map((type) => (
                    <li key={type.id}>
                        {type.name} - ${type.price} - {type.is_active ? 'Active' : 'Inactive'}
                        <button
                            onClick={() =>
                                handleDeleteEntity(api.deleteSubscriptionType, type.id, setSubscriptionTypes)
                            }
                        >
                            Delete
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default AdminPanel;
