import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/SubscriptionForm.css';

function SubscriptionForm() {
    const location = useLocation();
    const subscription = location.state?.subscription;

    const [cardNumber, setCardNumber] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [cvc, setCvc] = useState('');
    const [errors, setErrors] = useState({});

    const validateCardNumber = (number) => /^\d{16}$/.test(number);
    const validateExpiryDate = (date) => {
        const [month, year] = date.split('/');
        const isValidFormat = /^\d{2}\/\d{2}$/.test(date); // Проверка формата MM/YY
        const isValidMonth = month >= 1 && month <= 12; // Месяц от 01 до 12
        const currentYear = new Date().getFullYear() % 100; // Последние 2 цифры текущего года
        const isValidYear = year >= currentYear; // Год не меньше текущего
        return isValidFormat && isValidMonth && isValidYear;
    };
    const validateCvc = (code) => /^\d{3}$/.test(code);

    const handleExpiryDateChange = (e) => {
        let value = e.target.value.replace(/[^0-9]/g, ''); // Только цифры
        if (value.length > 2) {
            value = value.slice(0, 2) + '/' + value.slice(2); // Добавляем слэш после первых двух цифр
        }
        setExpiryDate(value.slice(0, 5)); // Ограничиваем длину до 5 символов
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const newErrors = {};

        if (!validateCardNumber(cardNumber)) newErrors.cardNumber = 'Номер карты должен содержать 16 цифр.';
        if (!validateExpiryDate(expiryDate)) newErrors.expiryDate = 'Срок действия карты должен быть в формате MM/YY.';
        if (!validateCvc(cvc)) newErrors.cvc = 'CVC должен содержать 3 цифры.';

        setErrors(newErrors);

        if (Object.keys(newErrors).length === 0) {
            alert(`Подписка "${subscription.name}" успешно оформлена!`);
        }
    };

    return (
        <div className="subscription-form-container">
            <h1>Оформление подписки</h1>
            <p>Вы выбрали подписку: {subscription.name}</p>
            <form onSubmit={handleSubmit}>
                <label>
                    Номер карты:
                    <input
                        type="text"
                        value={cardNumber}
                        onChange={(e) => setCardNumber(e.target.value.replace(/\D/g, ''))}
                        placeholder="1234 5678 9012 3456"
                        maxLength="16"
                        required
                    />
                    {errors.cardNumber && <span className="error">{errors.cardNumber}</span>}
                </label>
                <label>
                    Срок действия (MM/YY):
                    <input
                        type="text"
                        value={expiryDate}
                        onChange={handleExpiryDateChange}
                        placeholder="MM/YY"
                        required
                    />
                    {errors.expiryDate && <span className="error">{errors.expiryDate}</span>}
                </label>
                <label>
                    CVC:
                    <input
                        type="text"
                        value={cvc}
                        onChange={(e) => setCvc(e.target.value.replace(/\D/g, ''))}
                        placeholder="123"
                        maxLength="3"
                        required
                    />
                    {errors.cvc && <span className="error">{errors.cvc}</span>}
                </label>
                <button type="submit">Оплатить</button>
            </form>
        </div>
    );
}

export default SubscriptionForm;
