import React, { useEffect, useState } from 'react';
import api from '../services/api';
import Modal from '../components/Modal';
import '../styles/Dashboard.css';

function Dashboard() {
    const [subscriptions, setSubscriptions] = useState([]);
    const [purchases, setPurchases] = useState([]);
    const [userData, setUserData] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [isEditModalOpen, setEditModalOpen] = useState(false);
    const [isPasswordModalOpen, setPasswordModalOpen] = useState(false);

    const [editedUserData, setEditedUserData] = useState({
        name: '',
        email: '',
    });

    const [newPassword, setNewPassword] = useState('');

    const fetchUserData = async () => {
        try {
            const myIdResponse = await api.get('/myid');
            const userId = myIdResponse.data;

            const userResponse = await api.get(`/usercus/${userId}`);
            setUserData(userResponse.data);
        } catch (err) {
            setError('Не удалось загрузить данные.');
        } finally {
            setLoading(false);
        }
    };

    const handleSaveUserData = async () => {
        try {
            await api.put(`/usercus/${userData.id}`, editedUserData);
            setUserData((prev) => ({ ...prev, ...editedUserData }));
            setEditModalOpen(false);
        } catch (err) {
            alert('Не удалось обновить данные пользователя.');
        }
    };

    const handleChangePassword = async () => {
        try {
            await api.post(`/usercus/${userData.id}/change-password`, { password: newPassword });
            setPasswordModalOpen(false);
            alert('Пароль успешно изменён.');
        } catch (err) {
            alert('Не удалось сменить пароль.');
        }
    };

    useEffect(() => {
        fetchUserData();
    }, []);

    if (loading) {
        return <div className="dashboard-loading">Загрузка личного кабинета...</div>;
    }

    if (error) {
        return <div className="dashboard-error">Ошибка: {error}</div>;
    }

    return (
        <div className="dashboard-container">
            <h1>Личный кабинет</h1>

            <section className="user-info-section">
                <h2>Информация о пользователе</h2>
                <p><strong>Имя:</strong> {userData.name}</p>
                <p><strong>Email:</strong> {userData.email}</p>
                <button
                    onClick={() => {
                        setEditModalOpen(true);
                        setEditedUserData({ name: userData.name, email: userData.email });
                    }}
                    className="edit-user-button"
                >
                    Редактировать данные
                </button>
                <button
                    onClick={() => setPasswordModalOpen(true)}
                    className="change-password-button"
                >
                    Сменить пароль
                </button>
            </section>

            <Modal isOpen={isEditModalOpen} onClose={() => setEditModalOpen(false)}>
                <h2>Редактировать данные</h2>
                <label>
                    Имя:
                    <input
                        type="text"
                        value={editedUserData.name}
                        onChange={(e) =>
                            setEditedUserData((prev) => ({ ...prev, name: e.target.value }))
                        }
                    />
                </label>
                <label>
                    Email:
                    <input
                        type="email"
                        value={editedUserData.email}
                        onChange={(e) =>
                            setEditedUserData((prev) => ({ ...prev, email: e.target.value }))
                        }
                    />
                </label>
                <button onClick={handleSaveUserData} className="save-button">
                    Сохранить
                </button>
            </Modal>

            <Modal isOpen={isPasswordModalOpen} onClose={() => setPasswordModalOpen(false)}>
                <h2>Сменить пароль</h2>
                <label>
                    Новый пароль:
                    <input
                        type="password"
                        value={newPassword}
                        onChange={(e) => setNewPassword(e.target.value)}
                    />
                </label>
                <button onClick={handleChangePassword} className="save-button">
                    Сохранить
                </button>
            </Modal>
        </div>
    );
}

export default Dashboard;
