import React, { useEffect, useState } from 'react';
import api from '../services/api';

function Dashboard() {
    const [subscriptions, setSubscriptions] = useState([]);
    const [purchases, setPurchases] = useState([]);
    const [userData, setUserData] = useState({});
    const [editMode, setEditMode] = useState(false); // Режим редактирования
    const [editForm, setEditForm] = useState({ name: '', email: '', password: '' }); // Данные формы
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                console.log('Fetching user ID...');
                const myIdResponse = await api.get('/myid');
                const userId = myIdResponse.data; // ID пользователя
                console.log('User ID:', userId);

                console.log('Fetching user data...');
                const userResponse = await api.get(`/usercus/${userId}`);
                console.log('User Data:', userResponse.data);

                console.log('Fetching subscriptions and purchases...');
                const [subscriptionsResponse, purchasesResponse] = await Promise.all([
                    api.get(`/subscriptions/${userId}`), // Подписки пользователя
                    api.get('/courses'), // Покупки курсов пользователя
                ]);

                console.log('Subscriptions:', subscriptionsResponse.data);
                console.log('Courses:', purchasesResponse.data);

                // Устанавливаем данные в state
                setUserData(userResponse.data);
                setEditForm({
                    name: userResponse.data.name,
                    email: userResponse.data.email,
                    password: '',
                });
                setSubscriptions(subscriptionsResponse.data);
                setPurchases(purchasesResponse.data);
            } catch (err) {
                console.error('API Error:', err);
                setError('Не удалось загрузить данные.');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const handleEditChange = (e) => {
        const { name, value } = e.target;
        setEditForm((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleSaveChanges = async () => {
        try {
            const myIdResponse = await api.get('/myid');
            const userId = myIdResponse.data; // Получаем ID пользователя

            console.log('Saving changes...');
            const response = await api.put(`/usercus/${userId}`, {
                name: editForm.name,
                email: editForm.email,
                ...(editForm.password && { password: editForm.password }), // Отправляем пароль, только если он изменился
            });

            console.log('Updated User:', response.data);
            setUserData(response.data.user);
            setEditMode(false);
            alert('Данные успешно обновлены.');
        } catch (err) {
            console.error('Error updating user:', err);
            alert('Не удалось обновить данные.');
        }
    };

    if (loading) {
        return <div>Загрузка личного кабинета...</div>;
    }

    if (error) {
        return <div>Ошибка: {error}</div>;
    }

    return (
        <div>
            <h1>Личный кабинет</h1>

            <section>
                <h2>Информация о пользователе</h2>
                {editMode ? (
                    <form
                        onSubmit={(e) => {
                            e.preventDefault();
                            handleSaveChanges();
                        }}
                    >
                        <label>
                            Имя:
                            <input
                                type="text"
                                name="name"
                                value={editForm.name}
                                onChange={handleEditChange}
                                required
                            />
                        </label>
                        <br />
                        <label>
                            Email:
                            <input
                                type="email"
                                name="email"
                                value={editForm.email}
                                onChange={handleEditChange}
                                required
                            />
                        </label>
                        <br />
                        <label>
                            Новый пароль:
                            <input
                                type="password"
                                name="password"
                                value={editForm.password}
                                onChange={handleEditChange}
                            />
                        </label>
                        <br />
                        <button type="submit">Сохранить изменения</button>
                        <button type="button" onClick={() => setEditMode(false)}>
                            Отмена
                        </button>
                    </form>
                ) : (
                    <div>
                        <p><strong>Имя:</strong> {userData.name}</p>
                        <p><strong>Email:</strong> {userData.email}</p>
                        <button onClick={() => setEditMode(true)}>Редактировать</button>
                    </div>
                )}
            </section>

            {/* Подписки */}
            <section>
                <h2>Ваши подписки</h2>
                {subscriptions.length > 0 ? (
                    <ul>
                        {subscriptions.map((sub) => (
                            <li key={sub.id}>
                                <p><strong>Название подписки:</strong> {sub.subscription_type?.name || `Подписка ${sub.subscription_type_id}`}</p>
                                <p><strong>Статус:</strong> {sub.status}</p>
                                <p><strong>Дата начала:</strong> {sub.start_date}</p>
                                <p><strong>Дата окончания:</strong> {sub.end_date}</p>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>У вас нет активных подписок.</p>
                )}
            </section>

            {/* Курсы */}
            <section>
                <h2>Ваши курсы</h2>
                {purchases.length > 0 ? (
                    <ul>
                        {purchases.map((purchase) => (
                            <li key={purchase.id}>
                                <p><strong>Название курса:</strong> {purchase.course?.title || `Курс #${purchase.course_id}`}</p>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>Вы еще не приобрели ни одного курса.</p>
                )}
            </section>
        </div>
    );
}

export default Dashboard;
