import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';
import '../styles/CoursePage.css';

function CoursePage() {
    const { courseId } = useParams(); // ID курса из URL
    const navigate = useNavigate();
    const [course, setCourse] = useState(null);
    const [articles, setArticles] = useState([]);
    const [userAccess, setUserAccess] = useState({ has_course_access: false, has_subscription: false });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Загрузка данных курса, статей и проверки доступа
    useEffect(() => {
        const fetchCourseData = async () => {
            try {
                // Получаем данные курса
                const courseResponse = await api.get(`/courses/${courseId}`);
                setCourse(courseResponse.data);

                // Получаем статьи курса
                const articlesResponse = await api.get(`/subarticles/${courseId}`);
                setArticles(articlesResponse.data);

                // Проверяем доступ пользователя
                const userAccessResponse = await api.get(`/courses/${courseId}/access`);
                setUserAccess(userAccessResponse.data);
            } catch (err) {
                setError('Не удалось загрузить данные курса.');
            } finally {
                setLoading(false);
            }
        };

        fetchCourseData();
    }, [courseId]);

    // Покупка курса
    const handlePurchaseCourse = async () => {
        try {
            await api.post(`/courses/${courseId}/purchase`);
            alert('Курс успешно приобретён.');
            setUserAccess((prev) => ({ ...prev, has_course_access: true }));
        } catch (err) {
            alert('Не удалось приобрести курс.');
        }
    };

    if (loading) return <div className="course-page-loading">Загрузка курса...</div>;
    if (error) return <div className="course-page-error">Ошибка: {error}</div>;

    return (
        <div className="course-page-container">
            <h1>{course.title}</h1>
            <p className="course-description">{course.description}</p>

            <h2>Список статей</h2>
            <ul className="articles-list">
                {articles.map((article) => (
                    <li key={article.id} className="article-item">
                        {userAccess.has_course_access || userAccess.has_subscription ? (
                            <button
                                onClick={() => navigate(`/articles/${article.id}`)}
                                className="article-link"
                            >
                                {article.title}
                            </button>
                        ) : (
                            <span className="article-disabled">{article.title}</span>
                        )}
                    </li>
                ))}
            </ul>

            {!userAccess.has_course_access && !userAccess.has_subscription && (
                <button onClick={handlePurchaseCourse} className="purchase-button">
                    Приобрести курс
                </button>
            )}
        </div>
    );
}

export default CoursePage;
