import React, { useEffect, useState } from 'react';
import api, {
    fetchUsers,
    fetchSubscriptions,
    fetchSubscriptionTypes,
    createSubscription,
    updateSubscription,
    deleteSubscription,
} from '../services/api';
import Modal from '../components/Modal';
import '../styles/AdminPanel.css';
import { useNavigate } from 'react-router-dom';

function AdminPanel() {
    const [data, setData] = useState({
        users: [],
        subscriptions: [],
        subscriptionTypes: [],
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isModalOpen, setModalOpen] = useState(false);
    const [currentEdit, setCurrentEdit] = useState(null);
    const [editData, setEditData] = useState({});
    const [isAdding, setIsAdding] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [usersResponse, subscriptionsResponse, subscriptionTypesResponse] = await Promise.all([
                    fetchUsers(),
                    fetchSubscriptions(),
                    fetchSubscriptionTypes(),
                ]);

                setData({
                    users: usersResponse.data,
                    subscriptions: subscriptionsResponse.data,
                    subscriptionTypes: subscriptionTypesResponse.data,
                });
            } catch (err) {
                setError('Не удалось загрузить данные админ-панели');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const logout = () => {
        localStorage.removeItem('authToken');
        navigate('/login');
    };

    const handleAdd = () => {
        setIsAdding(true);
        setEditData({ user_id: '', subscription_type_id: '', start_date: '', end_date: '', status: 'Active' });
        setModalOpen(true);
    };

    const handleEdit = (subscription) => {
        setIsAdding(false);
        setEditData({ ...subscription });
        setModalOpen(true);
    };

    const handleDelete = async (id) => {
        try {
            await deleteSubscription(id);
            const response = await fetchSubscriptions();
            setData((prev) => ({ ...prev, subscriptions: response.data }));
        } catch (err) {
            alert('Не удалось удалить элемент');
        }
    };

    const handleSave = async () => {
        try {
            if (isAdding) {
                await createSubscription(editData);
            } else {
                await updateSubscription(editData.id, editData);
            }

            const response = await fetchSubscriptions();
            setData((prev) => ({ ...prev, subscriptions: response.data }));
        } catch (err) {
            alert('Не удалось сохранить изменения');
        } finally {
            setModalOpen(false);
        }
    };

    if (loading) return <div>Загрузка...</div>;
    if (error) return <div>Ошибка: {error}</div>;

    return (
        <div className="admin-panel-container">
            <h1>Админ-панель</h1>
            <button onClick={logout}>Выйти</button>
            <section>
                <h2>Подписки</h2>
                <button onClick={handleAdd}>Добавить подписку</button>
                <ul>
                    {data.subscriptions.map((subscription) => (
                        <li key={subscription.id}>
                            {subscription.user_id} - {subscription.subscription_type_id} ({subscription.status})
                            <button onClick={() => handleEdit(subscription)}>Редактировать</button>
                            <button onClick={() => handleDelete(subscription.id)}>Удалить</button>
                        </li>
                    ))}
                </ul>
            </section>
            <Modal isOpen={isModalOpen} onClose={() => setModalOpen(false)}>
                <h2>{isAdding ? 'Добавить подписку' : 'Редактировать подписку'}</h2>
                <form>
                    <label>
                        Пользователь:
                        <select
                            value={editData.user_id}
                            onChange={(e) => setEditData({ ...editData, user_id: e.target.value })}
                        >
                            <option value="">Выберите пользователя</option>
                            {data.users.map((user) => (
                                <option key={user.id} value={user.id}>
                                    {user.name}
                                </option>
                            ))}
                        </select>
                    </label>
                    <label>
                        Тип подписки:
                        <select
                            value={editData.subscription_type_id}
                            onChange={(e) => setEditData({ ...editData, subscription_type_id: e.target.value })}
                        >
                            <option value="">Выберите тип подписки</option>
                            {data.subscriptionTypes.map((type) => (
                                <option key={type.id} value={type.id}>
                                    {type.name}
                                </option>
                            ))}
                        </select>
                    </label>
                    <label>
                        Дата начала:
                        <input
                            type="date"
                            value={editData.start_date}
                            onChange={(e) => setEditData({ ...editData, start_date: e.target.value })}
                        />
                    </label>
                    <label>
                        Дата окончания:
                        <input
                            type="date"
                            value={editData.end_date}
                            onChange={(e) => setEditData({ ...editData, end_date: e.target.value })}
                        />
                    </label>
                    <label>
                        Статус:
                        <select
                            value={editData.status}
                            onChange={(e) => setEditData({ ...editData, status: e.target.value })}
                        >
                            <option value="Active">Active</option>
                            <option value="Paused">Paused</option>
                            <option value="Expired">Expired</option>
                        </select>
                    </label>
                    <button type="button" onClick={handleSave}>
                        Сохранить
                    </button>
                </form>
            </Modal>
        </div>
    );
}

export default AdminPanel;
