import React, { useEffect, useState } from 'react';
import api from '../services/api';
import Modal from '../components/Modal';
import '../styles/AdminPanel.css';

function AdminPanel() {
    const [users, setUsers] = useState([]);
    const [courses, setCourses] = useState([]);
    const [articles, setArticles] = useState([]);
    const [subscriptions, setSubscriptions] = useState([]);
    const [subscriptionTypes, setSubscriptionTypes] = useState([]);

    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [isModalOpen, setModalOpen] = useState(false); // Управление модальным окном
    const [currentEdit, setCurrentEdit] = useState(null); // Текущий редактируемый элемент
    const [editData, setEditData] = useState({}); // Данные для редактирования

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [
                    usersRes,
                    coursesRes,
                    articlesRes,
                    subscriptionsRes,
                    subscriptionTypesRes,
                ] = await Promise.all([
                    api.get('/usercus'),
                    api.get('/courses'),
                    api.get('/articles'),
                    api.get('/subscriptions'),
                    api.get('/subscription-types'),
                ]);
                setUsers(usersRes.data);
                setCourses(coursesRes.data);
                setArticles(articlesRes.data);
                setSubscriptions(subscriptionsRes.data);
                setSubscriptionTypes(subscriptionTypesRes.data);
            } catch (err) {
                setError('Failed to load admin data');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const createHandler = async (endpoint, data, setState, resetState) => {
        try {
            const response = await api.post(endpoint, data);
            setState((prev) => [...prev, response.data]);
            resetState();
        } catch (err) {
            alert(`Failed to create ${endpoint}`);
        }
    };

    const deleteHandler = async (endpoint, id, setState) => {
        try {
            await api.delete(`${endpoint}/${id}`);
            setState((prev) => prev.filter((item) => item.id !== id));
        } catch (err) {
            alert(`Failed to delete ${endpoint}`);
        }
    };

    const handleEdit = (item, endpoint) => {
        setCurrentEdit(endpoint);
        setEditData(item);
        setModalOpen(true);
    };

    const handleSave = async () => {
        try {
            await api.put(`/${currentEdit}/${editData.id}`, editData);
            alert('Изменения успешно сохранены.');

            switch (currentEdit) {
                case 'usercus':
                    setUsers((prev) =>
                        prev.map((user) => (user.id === editData.id ? editData : user))
                    );
                    break;
                case 'courses':
                    setCourses((prev) =>
                        prev.map((course) => (course.id === editData.id ? editData : course))
                    );
                    break;
                case 'articles':
                    setArticles((prev) =>
                        prev.map((article) => (article.id === editData.id ? editData : article))
                    );
                    break;
                case 'subscriptions':
                    setSubscriptions((prev) =>
                        prev.map((sub) => (sub.id === editData.id ? editData : sub))
                    );
                    break;
                case 'subscription-types':
                    setSubscriptionTypes((prev) =>
                        prev.map((type) => (type.id === editData.id ? editData : type))
                    );
                    break;
                default:
                    break;
            }

            setModalOpen(false);
        } catch (err) {
            alert('Не удалось сохранить изменения.');
        }
    };

    if (loading) return <div className="admin-panel-container">Загрузка...</div>;
    if (error) return <div className="admin-panel-container">Ошибка: {error}</div>;

    return (
        <div className="admin-panel-container">
            <h1>Admin Panel</h1>

            {/* Users Section */}
            <section className="admin-section">
                <h2>Users</h2>
                src/pages/AdminPanel.js
                Line 147:52:  'openModal' is not defined  no-undef
                <ul className="admin-list">
                    {users.map((user) => (
                        <li key={user.id} className="admin-list-item">
                            <span>{user.name} - {user.email}</span>

                            <div className="admin-buttons">
                                <button
                                    className="admin-edit-button"
                                    onClick={() => handleEdit(user, 'usercus')}
                                >
                                    Edit
                                </button>
                                <button
                                    className="admin-delete-button"
                                    onClick={() => deleteHandler('/usercus', user.id, setUsers)}
                                >
                                    Delete
                                </button>

                            </div>
                        </li>
                    ))}
                </ul>
            </section>

            {/* Courses Section */}
            <section className="admin-section">
                <h2>Courses</h2>
                <ul className="admin-list">
                    {courses.map((course) => (
                        <li key={course.id} className="admin-list-item">
                            <span>{course.title}</span>
                            <div className="admin-buttons">
                                <button
                                    className="admin-edit-button"
                                    onClick={() => handleEdit(course, 'courses')}
                                >
                                    Edit
                                </button>
                                <button
                                    className="admin-delete-button"
                                    onClick={() =>
                                        deleteHandler('/courses', course.id, setCourses)
                                    }
                                >
                                    Delete
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            </section>

            {/* Articles Section */}
            <section className="admin-section">
                <h2>Articles</h2>
                <ul className="admin-list">
                    {articles.map((article) => (
                        <li key={article.id} className="admin-list-item">
                            <span>{article.title}</span>
                            <div className="admin-buttons">
                                <button
                                    className="admin-edit-button"
                                    onClick={() => handleEdit(article, 'articles')}
                                >
                                    Edit
                                </button>
                                <button
                                    className="admin-delete-button"
                                    onClick={() =>
                                        deleteHandler('/articles', article.id, setArticles)
                                    }
                                >
                                    Delete
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            </section>

            {/* Subscriptions Section */}
            <section className="admin-section">
                <h2>Subscriptions</h2>
                <ul className="admin-list">
                    {subscriptions.map((sub) => (
                        <li key={sub.id} className="admin-list-item">
                            <span>User: {sub.user_id}, Type: {sub.subscription_type_id}</span>
                            <div className="admin-buttons">
                                <button
                                    className="admin-edit-button"
                                    onClick={() => handleEdit(sub, 'subscriptions')}
                                >
                                    Edit
                                </button>
                                <button
                                    className="admin-delete-button"
                                    onClick={() =>
                                        deleteHandler('/subscriptions', sub.id, setSubscriptions)
                                    }
                                >
                                    Delete
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            </section>

            {/* Subscription Types Section */}
            <section className="admin-section">
                <h2>Subscription Types</h2>
                <ul className="admin-list">
                    {subscriptionTypes.map((type) => (
                        <li key={type.id} className="admin-list-item">
                            <span>{type.name} - ${type.price} - {type.is_active ? 'Active' : 'Inactive'}</span>
                            <div className="admin-buttons">
                                <button
                                    className="admin-edit-button"
                                    onClick={() => handleEdit(type, 'subscription-types')}
                                >
                                    Edit
                                </button>
                                <button
                                    className="admin-delete-button"
                                    onClick={() =>
                                        deleteHandler('/subscription-types', type.id, setSubscriptionTypes)
                                    }
                                >
                                    Delete
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            </section>

            {/* Modal */}
            <Modal isOpen={isModalOpen} onClose={() => setModalOpen(false)}>
                <h2>Редактирование</h2>
                {editData &&
                    Object.keys(editData).map((key) => (
                        <label key={key}>
                            {key}:
                            <input
                                type={typeof editData[key] === 'boolean' ? 'checkbox' : 'text'}
                                name={key}
                                value={
                                    typeof editData[key] === 'boolean'
                                        ? undefined
                                        : editData[key]
                                }
                                checked={
                                    typeof editData[key] === 'boolean' ? editData[key] : undefined
                                }
                                onChange={(e) => {
                                    const { name, value, type, checked } = e.target;
                                    setEditData((prev) => ({
                                        ...prev,
                                        [name]: type === 'checkbox' ? checked : value,
                                    }));
                                }}
                            />
                        </label>
                    ))}
                <button className="modal-save-button" onClick={handleSave}>
                    Сохранить
                </button>
            </Modal>
        </div>
    );
}

export default AdminPanel;
